mapper.insert:



	SELECT IFNULL(MAX(BF_NUM), 0) + 1 
	FROM board_file
	WHERE BOARD_NUM = #{board_num}



INSERT 
INTO board_file
(
	  BOARD_NUM
	, BF_NUM
	, BF_NAME_KEY
	, BF_NAME
	, BF_PATH
	, BF_SIZE
	, BF_DELETE_YN 
	, M_ID
	, BF_INSERT_DATE
	, BF_UPDATE_DATE
)
VALUES
(
	  #{board_num}
	, #{bf_num}
	, #{bf_name_key}
	, #{bf_name}
	, #{bf_path}
	, #{bf_size}
	, 'N'
	, 'NONMEMBER'
	, NOW()
	, NOW() 
)

mapper.delete:

		UPDATE freedom_board 
		   SET FB_TITLE = #{fb_title}
		     , FB_CONTENT = #{fb_content}
		     , FB_UPDATE_DATE = NOW()
 		WHERE BOARD_NUM = #{board_num}


		UPDATE board_file 
		   SET BF_DELETE_YN = 'Y'
		     , BF_UPDATE_DATE = NOW()
 		WHERE BOARD_NUM = #{board_num}
 		  AND BF_NUM = #{bf_num}
