mapper.insert:
     			INSERT INTO member(m_id, m_pw, m_name, m_nickname, m_phonenum, m_email, m_birth) 
     							VALUES(    #{m_id} 
				                 		 , sha2(#{m_pw},512)
				                 		 , #{m_name}
				                 		 , #{m_nickname}
				                 		 , #{m_phoneNum}
				                 		 , #{m_email}
				                 		 , #{m_birth})

mapper.update:
UPDATE member SET m_name = #{m_name}
, m_nickname = #{m_nickname}
,  m_email = #{m_email}
, m_phonenum = #{m_phoneNum}
, m_address = #{m_address}
, m_birth = #{m_birth}
WHERE m_id = #{m_id}
